    private byte[] sentMsg() {
        String msg = "";
        switch (this.client_imev) {
            case "359633100458591":
                msg = "";
                break;
            case "359633100458592":
                msg = "";
                break;
            default:
        }
        return DatatypeConverter.parseHexBinary(msg);
    }

